<script>
    (function($){
        //Open & Close Hamburger Menu
        $('.nav--toggle').on('click', function () {
           $(".nav--toggle, .nav--container, .nav--secondary, .menu").toggleClass('active');
           //Prevent scrolling when nav is open
            var target = document.querySelector("#nav");
            if($('.nav--container').hasClass('active')) {
                bodyScrollLock.disableBodyScroll(target);
            } else {
                bodyScrollLock.enableBodyScroll(target);
            }
        });

       //Smooth scrolling to anchors
        $(function () {
            $('a[href*="#"]:not([href="#"])').bind('touchend click', function () {
                if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
                    var target = $(this.hash);
                    target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
                    if (target.length) {
                        $('html, body').animate({
                            scrollTop: target.offset().top - 80
                        }, 1000);
                        return false;
                    }
                }
            });
        });

        //Inview.js for animations
        $('.animate').bind('inview', function(event, inview){
            if(inview){
                $(this).addClass('active');
            }
        });

        $('.prism--container').on('inview', function(inview) {
            if(inview){
                $(this).append('<img src="<?=$host?>/wp-content/themes/bones_3.0/library/images/IvioPrism.gif" class="nav--prism" alt="Ivio Prism"/>')
                if(!$('.nav--secondary').hasClass('active'))   {
                    $(this).empty();
                }
            }
        });

        //Sticky Nav transition
        $(window).on("scroll", function () {
            var scroll = $(window).scrollTop(),
                width = $(window).width(),
                heroHeight = 85;

            if ((scroll > heroHeight)) {
                $(".header--main").addClass("nav-sticky");
                $("#wrapper").css("margin-top" , "71px");

            } else if ((scroll < heroHeight)) {
                $(".header--main").removeClass("nav-sticky");
                $("#wrapper").css("margin-top" , "0");

            } else {
                $(".header--main").removeClass("nav-sticky");
                $("#wrapper").css("margin-top" , "0");
            }
        });

        objectFitImages();
})(jQuery);
</script>

